  }
  .timeline-header {
    .th {
      float: left;
      margin: 0px 10px 0px 10px;
      font-weight: bold;
      color:$dark-text-color;
    }
    .new a {
      color: $dark-text-color;
      background-color: $bright-text-color;
      border: 1px solid #cdcdcd;
      font-weight: normal;
      font-size: 0.8em;
      padding: 2px 5px 2px 5px;
    }
  }
  .timeline-body {
    width: 100%;
    float: left;
    .timeline-data {
      width: 100%;
      margin-left: -10px;
     }
    .metric-year, .metric-unit {
      font-size: 12px;
      }
    .timeline-row {
        position: relative;
        clear: both;
        padding: 7px 0px;
        font-size: 12px;
        width: 100%;
        height: 45px;
        color: $dark-text-color;
      &:hover {
        background-color: #F3F3F3;
      }
      .td {
        float: left;
        margin: 0px 10px 0px 10px;
        .metric-value {
          font-size: 20px;
        }
      }
      .credit {
        padding: 10px 5px;
      }
      .year {
        padding: 8px 0px;
      }
      // .year, .credit {
      //   padding-top: 2px;
      // }
      .search-result-list {
        display: inline-block;
        padding: 0px;
        .search-result-item {
          display: inline;
          padding-left: 0;
        }
      }
    }

  }
  .timeline-dot {
    position: absolute;
    top: 20px;
    left: 46px;
    height: 5px;
    width: 5px;
    background: gray;
    z-index: 10;
  }
  .timeline-line {
    position: absolute;
    top: 21px;
    left: 48px;
    height: 53px;
    width: 1px;
    border-left: 1px solid #CCCCCC;
    z-index: 9;
  }
}

