@import url("http://fonts.googleapis.com/css?family=Droid+Sans:400,700");

//***ALL COLOR VARS & MIXINS***//
$heading_color: #555;
$body_background: #ebebeb;
$primary_color: #555;
$anchor_color: #03998D;
$body-txt-color: #777;
$wanted-card-color: #b6060a;
$closed-content-a-color: #729;
$infographic-heading: #949494;
$button-color: #03998D;

//claim colors
$claim-tag-companybg: #D3741C;
$claim-tag-companybg-hover:#BB5B00;
$claim-tag-topicbg:#2B7EA0;
$claim-tag-topicbg-hover:#01648D;

//navigation colors
$nav-srch-background:#FFF;
$nav-srch-txt-color:#a8a8a8;
$navmenu-anchor-color: #fff;
$navmenu-hover-bgcolor: #03998D;
$navsbmenu-bgcolor: #03998D;
$navsbmenu-anchor-color: #00AC9E;

@mixin searchbox-color{
  background: $nav-srch-background !important;
  color: $nav-srch-txt-color !important;
}
@mixin searchbox-color-focus{
  background-color: #f3f4de !important;
  color: #a8a8a8 !important;
}

$card-header-color: #dbdbdb;

//card colors
@mixin card-txt-color{
  color: #646464;
  a {
    color: #646464;
  }
}
//***COLOR VARS - END***//


body {
  color: $body-txt-color;
  font-family: 'Droid Sans', sans-serif;
  line-height: 1.5em;
  font-size: 0.9em;
}

body#wagn {
  min-height: 100%;
  min-width: 480px;
  background: $body_background;
  padding: 0;
  margin: 0;
}

body#tinymce {
   padding: 4px;
}

a {
  color: $anchor_color;
}

a.wanted-card {
  color: $wanted-card-color;
}

/* many sites may want this external link behavior; it's here because it's a nuisance to override when not desired */
a.external-link,
.closed-content a.external-link {
  color: $closed-content-a-color;
  background: url(/assets/external_link.png) center right no-repeat;
  padding-right: 13px !important;
}

img {
  border: 0
}

h1, h2 {
  color: $heading_color;
}

h1 {
  font-size: 1.2em;
  font-weight: normal;
  line-height: 1.8em;
  padding: 5px 0;
}

// Search box
input.navbox {
  @include searchbox-color;
  border: 0px solid #AAA !important;
  padding-left: 3px;
  border-radius: 3px;

  &:focus {
    @include searchbox-color-focus;
    outline: none;
  }
}

#logo-bar {
  padding: 0 3%;
  background:#fff;
  img {
    margin-top: 3px;
    height: 40px;
    border:none;
  }
  #logging {
    float:right;
    font-size: 1.1em;
    margin-top: 10px;
    a {
      /*color: #72b671;*/
      color: $anchor_color;
      /*text-shadow: #fff 1px 1px 0px;*/
      margin-left: 10px;
    }
    a:hover {
      color: $body-txt-color;
    }
  }
}


#menu-bar {
  background: $primary_color;
  padding: 0 3%;
  // border-bottom: 1px solid #999;
  // -moz-box-shadow: 0px 0px 2px 0px #ccc;
  // -webkit-box-shadow: 0px 0px 2px 0px #ccc;
  // box-shadow: 0px 0px 2px 0px #ccc;
  // text-shadow: #5c995b 1px 1px 0px;
  margin-bottom: 20px;

  .navbox-form {
    display: inline-block;
    float: right;
    width: 33%
  }
  .navbox {
    height: 25px;
    width: 100%;
    margin-top: 8px;
  }
  a {
    color: $navmenu-anchor-color;
    display: block;
    margin: 0;
    text-transform: uppercase;
    letter-spacing: 0.1em;
  }

  .main-links {
    min-height: 40px;
    > div {
      float: left;
      padding: 10px 3%;
      cursor: pointer;
      span {
        display: block;
        margin: 0;
        text-transform: uppercase;
        letter-spacing: 0.1em;
        color: $navmenu-anchor-color;
      }
    }
    > div:hover {
      background: $navmenu-hover-bgcolor;
    }
  }


  ul {
    display: none;
    position: absolute;
    z-index: 1;
    border-radius: 0;
    border: none;
    padding: 0;
    > li {
      padding: 0;
    }
    a {
      border: 0;
      font-size: 0.9em;
      width: auto;
      min-width: 120px;
      padding: 5px 28px;
      background: $navsbmenu-bgcolor;
      border-radius: inherit;
    }
    a:hover {
      background: $navsbmenu-anchor-color;
    }
  }
}

.more-link {
  float: right;
}

.card-header {
  font-family: 'Droid Sans';
}

.card-frame {
  border-radius: 4px;
  border: 0px solid #E5E5E5;
  > .card-header {
    background: $card-header-color;
font-size: 1.1em;
// font-weight: bold;
    @include card-txt-color;
  }
  > .instruction {
    background: #bbb;
  }
}


#main {
  font-size: 1.1em;
  clear: both;
  padding: 20px 3%;
  .card-frame > .card-body > .titled-view {
    margin: 15px 0;
    > .card-body {
      margin: 5px 0 5px 3%;
    }
    > .card-content {
      padding: 10px 20px 15px 20px;
    }
  }
  .card-frame {
    border-radius: 4px;
    > .card-body > h1 {
      font-size: 2em;
      font-weight: normal;
      line-height: 2em;
      padding: 5px 0 0 0;
    }
  }
  > .card-slot > h1 {
    font-size: 1.5em;
    padding: 0 5px;
    .card-title {
      text-align: center;
      display: inline-block;
      width: 90%;
      text-indent: 10%;
    }
  }
}
.card-slot > .card-header .card-menu-link {
  display: none;
}
.card-slot.closed-view,
.card-slot:hover {
  > .card-header .card-menu-link {
    display: inline-block;
    margin-left: 10px;
  }
}




.revision-navigation,
.current {
  background: #FFFFFF;
}


body.home-layout #main {
  > .card-content {
    .SELF-wikirate_infographic {
      text-align: center;
      background: #fff;
      padding: 10px;
      border-radius: 4px;
      -moz-border-radius: 4px;
      border: 0px solid #E5E5E5;
      > .SELF-infographic {
        float: left;
        display: inline-block;
        width:68%;
      }
      span {
       font-size: 1.8em;
       color: $infographic-heading;
      }
      p {
        line-height: 2em;
        color: #d5d5d5;
        font-size: 1.3em;
      }
      > .wikirate-video-container {
        position: relative;
        padding-bottom: 17%;
        padding-top: 0px;
        height: 0;
        overflow: hidden;
        left: 14px;
        top: -14px;
        width: 30.2%;
        margin: 0px;
        iframe {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
        }
      }
    }
    .SELF-industry_of_the_month.content-view {
      min-width: 90%;
      text-align: center;
      padding: 20px 3%;
    }
    .home-left {
      width: 65%;
      float: left;
      .SELF-featured_topic.card-frame {
        margin-top: 40px;
      }
    }
    .home-right {
      width: 33%;
      float: right;
    }
    > #wikirate_tagline {
      text-align: center;
      overflow: visible;
      font-size: 2em;
      padding: 1.5em;
    }
  }
}


.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
  clear: both;
  padding: 20px 0;
  min-height: 200px;
}

.STRUCTURE-company_feature,
.STRUCTURE-topic_feature {
  > a {
    font-size: 1.3em;
  }

  h1 {
    font-size: 0.9em;
    font-style: italic;
    color: #aaa;
  }

  .TYPE-image.content-view {
    width: 25%;
    max-width: 100px;
    padding: 0 3% 10px 0;
    min-height: 200px;

    img {
        border-radius: 10px;
       -moz-border-radius: 10px;
    }
  }
}

/*~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/

body > .SELF-wikirate_footer {
  clear: both;
  display: block;
  padding: 45px 30px 10px 30px;
  text-align: center;
  font-size: 0.9em;
  background: #fff;
  border: 1px solid #E5E5E5;
a.external-link {
  padding-right: 0 !important;
  background: inherit !important;
}
[class*="SELF-logo_"] {
display: inline-block;
}
}

#credit,
#credit a {
  padding: 0 !important;
  background: none;
  border: 0px;
  margin-bottom: 15px;
  position: relative;
}

#credit {
  z-index: -0;
  left: 8px;
  display: block;
  a {
    right: 16px;
    position: relative;
    z-index: -1;
  }
}

.RIGHT-claim_count.content-view {
  display: inline;
  float: right;
  color: #999;
}

.search-result-item > .TYPE-page.content-view {
  .RIGHT-website .pointer-list {
    font-size: 1.1em;
    font-weight: bold;
    padding-bottom: 0;
  }
  .RIGHT-title {
    font-size: 0.9em;
    margin: 0 0 7px 3%;
  }
}



.RIGHT-image_source.content-view {
  font-size: 0.8em;
  font-style: italic;
}

.TYPE-analysi,
.TYPE-company,
.TYPE-topic {
  &.STRUCTURE-analysi_comparison a.internal-link,
  >.source-side > a {
    display: block;
    padding: 10px;
    margin: 10px 5%;
    float: right;
    border: 2px solid #555;
    border-radius: 10px;
    background: #eee;
    font-size: 1.2em;
  }
}
.TYPE-analysi {
  &.STRUCTURE-analysi_comparison a.internal-link,
  >.source-side > a {
    float: none;
    text-align: center;
  }
}



.TYPE-topic .STRUCTURE-topic_analysi_blurb,
.TYPE-company .STRUCTURE-company_analysi_blurb {
  padding: 20px 0 20px 5%;
  border-radius: 10px;
  -moz-border-radius: 10px;
  border-bottom: 1px solid #eee;
  > h1 {
    font-size: 1.2em;
  }
  > a {
    float: right;
  }
}

.STRUCTURE-analysi_blurb {
  > .card-body,
  .RIGHT-claim {
    padding-left: 5%;
  }
  .RIGHT-claim {
    > .card-content {
      padding: 0 0 0 10%;
    }
    .search-result-item {
      padding-bottom: 10px;
    }
  }
  > a {
    display: inline-block;
    color: #555;
    font-style: italic;
    padding-top: 5px;
  }
}


.STRUCTURE-icon_and_link.content-view > a > img,
.STRUCTURE-company_analysi_link.content-view > a > img,
.STRUCTURE-topic_analysi_link.content-view > a > img,
.STRUCTURE-company_link.content-view > a > img,
.STRUCTURE-topic_link.content-view > a > img {
  border-radius: 3px;
  -moz-border-radius: 3px;
  margin-right: 5px;
}

.STRUCTURE-topic_analysi_link_and_compare .STRUCTURE-compare_link,
.RIGHT-link_and_compare .RIGHT-compare_link {
  float: right;
}

.STRUCTURE-claim_summary {
  > h1 {
    font-size: 1.1em;
    padding-bottom: 0;
  }
  > .card-body {
    padding:  5px 0 18px 5%;
    margin: 0 0 5px 0px;
    border-bottom: 1px solid #eee;
    font-size: 0.8em;
    .fa {
      padding-right: 5px;
    }
    > div {
      margin-left: 5%;
      padding: 2px 0;
      text-indent: -3%;
      div {
        display: inline;
      }
    }
    .missing-view {
      display: none;
    }
    .RIGHT-source .source-title {
      font-style: italic;
    }
    .pointer-item, .search-result-item {
       text-indent: 10px;
       margin: 0;
       margin-right: 0px !important;
       padding: 2px;
      :after {
        content: ' ';
      }
    }
  }
}

#claim-sourcelink .pointer-item a {
  color: $body-txt-color;
    &:hover{
      color:$anchor_color;
    }
 }
 //.TYPE-claim.open-view .known-card
#claim-tag .known-card, .TYPE-claim.open-view .RIGHT-company .known-card, .RIGHT-topic .known-card {
padding: 2px 3px !important;
border-radius: 3px;
font-size: 0.9em;
color: #fff;
text-decoration: none;
}
//, .TYPE-claim.open-view .RIGHT-tag .known-card
#claim-tag .known-card{
background:#ccc;
}


#claim-tag .RIGHT-company> .pointer-list .known-card, .TYPE-claim.open-view .RIGHT-company .known-card{
background: $claim-tag-companybg;
&:hover{
background: $claim-tag-companybg-hover;
}
}
#claim-tag .RIGHT-topic> .pointer-list .known-card, .TYPE-claim.open-view .RIGHT-topic .known-card{
background:  $claim-tag-topicbg;
&:hover{
background: $claim-tag-topicbg-hover;
}
}

#main > .SELF-topic,
#main > .SELF-company {
  > .card-content > .TYPE-search.card-content > .search-result-list > .search-result-item {
    display: inline-block;
    min-width: 130px;
    width: 17%;
    padding: 10px 1%;
  }
}

.STRUCTURE-topic_overview,
.STRUCTURE-company_overview {
  box-shadow: 0px 0px 12px 1px rgba(221, 221, 221, 0.87);
  border-radius: 10px;
  -moz-border-radius: 10px;
  text-align: center;
  border: 1px solid rgb(213, 213, 213);
  width: 100%;
  min-height: 250px;
  > a {
    font-size: 1em;
    line-height: 1.6em;
    display: block;
    padding: 5px;
  }
  :hover{
    background: #f8f8f8;
  }
  .card-title {
    display: inline-block;
    height: 60px;
  }
  .TYPE-image {
    width: 80%;
    padding: 5px 10%;
    height: 150px;
    overflow: hidden;
    img {
      max-height: 100%;
    }
  }
  .claim-count {
    color: #999;
    .RIGHT-claim_count {
      color: #444;
      float: none;
    }
  }
}


.STRUCTURE-labeled_source_frame {
  label {
    display: inline-block;
    padding-right: 10px;
    float: left;
  }
  .RIGHT-title {
    display: inline-block;
    font-size: 1.2em;
    line-height: 1.2em;
  }
  em {
    float: right;
  }
  .pointer-list,
  .pointer-item {
    display: inline;
    padding-left: 0;
  }
}


fieldset.RIGHT-link input.card-content {
  margin-bottom: 30px;
}



.comment-box {
  background: inherit;
}

.TYPE-analysi.card-content {
  .search-no-results {
    display: none;
  }
  > .search-result-list {
    padding: 0;
  }
  .SELF-analysi_image img {
    height: 50px;
    padding-bottom: 25px;
  }
  .side-by-side {
    .RIGHT-logo,
    .SELF-analysi_image,
    .RIGHT-image {
      display: inline-block;
      padding: 10px;
    }
    text-align: center;
    > a {
      > .RIGHT-logo img,
      > .RIGHT-image img {
        border: 4px solid #666;
        border-radius: 10px;
        -moz-border-radius: 10px;
        max-height: 130px;
      }
    }
  }
  > .source-side > .TYPE-search > .card-content {
    padding-left: 3%;
  }
  .RIGHT-right_sidebar.card-content {
    > h1 {
      padding: 0;
    }
    > h2 {
      font-weight: normal;
      font-size: 1em;
      padding: 0 0 15px 3%;
    }
    > .card-content  {
      padding-bottom: 25px;
    }
  }
}

.RIGHT-right_sidebar .source-title {
  display: block;
  padding: 3px 0 3px 5%
}
.RIGHT-wikirate_source .content-editor select {
  height: 200px;
}

.STRUCTURE-website_summary.titled-view {
  padding-bottom: 20px;
  border-bottom: 1px solid #ddd;
  margin-bottom: 20px;
  > .card-content {
    padding-left: 5%;
  }
  .RIGHT-page {
    > h1 {
      font-size: 0.8em;
    }
    padding-left: 5%;
    .search-result-item {
      line-height: 2em;
    }
  }
}


.SELF-company_comparison.card-content {
  table {
    width: 100%;
  }
  th {
    padding: 5px 1%;
  }
  td {
    padding: 5px 2%;
    width: 50%
  }
  > div {
    width: 46%;
    padding: 5px 2%;
  }
  .left-side {
    float: left;
  }
  .right-side {
    float: right;
  }

}

.closed-content label {
  display: inline;
}

.TYPE_PLUS_RIGHT-company-label img {
  margin: 0 2px 5px 0;
  border-radius: 4px;
  -moz-border-radius: 4px;
}

fieldset {
 > .button-area,
 > .editor,
 > legend,
 > legend > .instruction {
   float: none;
   text-align: left;
   display:inline-block;
   width: 90%;
   padding: 0 5%;
  position:relative;

>p {
display: none;
position: absolute;
left: 18px;
opacity: 0.8;
top: 50%;
margin-top: -18px;
margin-left: 8px;
width: auto;
color: #FFFFFF;
background: #000000;
height: 30px;
line-height: 30px;
padding: 2px 10px;
text-align: center;
visibility: visible;
border-radius: 6px;
font-size: 0.8em;

&:after {
content: '';
position: absolute;
top: 50%;
right: 100%;
margin-top: -8px;
width: 0;
height: 0;
border-right: 8px solid #000000;
border-top: 8px solid transparent;
border-bottom: 8px solid transparent;
}
}
 }
 > legend
   h2{
     display:inline;
   }
 > legend > .instruction {
   padding: 4px 0;
   width: 70%;
 }
}


.source-side, .meta-side{
float: left;
padding: 0;
margin-left: 2.5%;
}
.TYPE-analysi .source-side,.TYPE-company .source-side,.TYPE-topic .source-side{
width: 65%;
}
.TYPE-analysi .meta-side,.TYPE-company .meta-side,.TYPE-topic .meta-side{
width: 30%;
}

.TYPE-claim .creator-credit{
  text-align:center;
  .search-result-list{
    display:inline-block;
  }
}
.TYPE-claim .preview-visit-link{
  float:right;
}
.TYPE-claim .analysis-link{
  margin: 3px 0px;
  float: left;
  width: 100%;
}
.TYPE-claim .remove-source{
  display:none;
  float:right;
  cursor:pointer; 
}

.TYPE-claim .source-side {
 width: 45%;
margin-left: 2.5%;
margin-right: 2.5%;
margin-top:20px;
  iframe {
    width: 100%;
    height: 500px;
  }
}

.TYPE-claim .meta-side {
width: 44.5%;
position: static;
margin: 0 0%;
  > .card-slot {
    margin: 20px 0;
  }
}
.TYPE-claim .meta-side .RIGHT-source, .sourcebox > input{
width:100%;
.sourcebox {
      background: #FAFAFA;
      display: inline-block;
      height: 66px;
      width: 96%;
     padding: 12px;
  >button {
      float: right;
      margin: 11px 0px;
     }
  }
.RIGHT-title{
font-weight: bold;
margin: 10px 0px;
 }
.TYPE-page{
  background: #fafafa;
  margin: 22px 0px;
  padding: 4px 20px 20px 20px;
 }
}

.RIGHT-compare_link.card-content,
.STRUCTURE-compare_link {
  &.card-content.content-view {
    display: inline;
    a {
      background: $button-color;
      font-size: 10px;
      padding: 3px 5px;
      margin-left: 10px;
      color: white;
      border-radius: 3px;
      -moz-border-radius: 3px;
    }
  }
}

@media all and (max-width: 1280px) and (min-width: 1024px) {
body>.SELF-wikirate_footer > .consortium-logo-container {
width: 102%;
}
}

#social-icons a {
 display: inline-block;
}

.claim-counting {
 float: right;
 margin-right: 6%;
}

.RIGHT-company > div> select {
 width: 40%;
}
.RIGHT-topic > div> select {
 width: 40%;
}
.claim_p_disclosure_link{
margin-left: 7.5%;
margin-bottom: 26px;
float: left;
}

.RIGHT-company>div>div>.chosen-choices li.search-choice {
background: #d3741c;
border: none;
box-shadow: none;
color: #fff;
padding: 7px 8px 8px 6px;
}
.RIGHT-topic>div>div>.chosen-choices li.search-choice {
background: #2b7ea0;
border: none;
box-shadow: none;
color: #fff;
padding: 7px 8px 8px 6px;
}

.claim-tip{
background: #FFF9B3;
text-align: center;
padding: 8px;
position: relative;
span{
position: absolute;
right: 2px;
top: 2px;
cursor:pointer;
}
}

.analysis-link a.company,a.topic{
padding: 2px 8px !important;
font-size: 0.9em;
text-decoration: none;
float: left;
color:#fff;
}

.analysis-link a.company {
background: $claim-tag-companybg;
border-bottom-left-radius: 3px;
border-top-left-radius: 3px;
&:hover{
background: $claim-tag-companybg-hover;
}
}
.analysis-link a.topic {
background:  $claim-tag-topicbg;
margin-right: 10px;
border-bottom-right-radius: 3px;
border-top-right-radius: 3px;
&:hover{
background: $claim-tag-topicbg-hover;
}
}

